  $(function () {

      var AppState = Backbone.Model.extend({
          defaults: {
              username: "",
              state: "start"
          }
      });

      var appState = new AppState();


      var Feed = Backbone.Model.extend({ // Модель пользователя
          defaults: {
              "href": "",
              "weight": 0/*,
          "rate": "",
          "title": "",
          "date": "",
